import React, { useState } from 'react'
import { Link, useNavigate } from 'react-router-dom'
import './Login.css'
import { auth } from './firebase';

function Login() {
    const history = useNavigate();
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const signIn = e =>{
        e.preventDefault();
        auth.signInWithEmailAndPassword(email, password).then((auth) => {
            if(auth){
                history('/')
            }
        })
    }
    const register = e =>{
        e.preventDefault();
        auth.createUserWithEmailAndPassword(email, password).then((auth) => {
            if(auth){
                history('/')
            }
        })
        .catch(error => alert(error.message))
    }
  return (
    <div className='login'>
        <Link to='/'>
            <img className='login__logo' src='https://upload.wikimedia.org/wikipedia/commons/thumb/a/a9/Amazon_logo.svg/1024px-Amazon_logo.svg.png' ></img>
        </Link>
        <div className='login__container'>
            <h1>Sign-In</h1>
            <form>
                <h5>E-mail</h5>
                <input type='text' value={email} onChange={e => setEmail(e.target.value)}/>
                <h5>Password</h5>
                <input type ='password' value={password} onChange = {e => setPassword(e.target.value)}></input>
                <button type = 'submit' onClick={signIn} className='login__signInButton'>Sign-In</button>
            </form>
            <p>
            By continuing, you agree to Amazon's Conditions of Use and Privacy Notice.
            </p>

            <button onClick = {register} type = 'submit' className='login__registerButton'>Create your Amazon account</button>
        </div>
    </div>
  )
}

export default Login
