import { CardElement, useElements, useStripe } from '@stripe/react-stripe-js'
import React, { useState } from 'react'
import CurrencyFormat from 'react-currency-format'
import { Link, Navigate, useNavigate } from 'react-router-dom'
import CheckoutProduct from './CheckoutProduct'
import './Payment.css'
import axios from 'axios'
import { useStateValue } from './StateProvider'
import { useEffect } from 'react'
function Payment() {
    const [{basket, user}, dispatch] = useStateValue();
    const stripe = useStripe();
    const elements = useElements()
    const [error, setError] = useState(null);
    const [disabled, setDisabled] = useState(true);
    const [succeeded, setSucceeded] = useState(false);
    const [processing, setProcessing] = useState("");
    const [clientSecret, setClientSecret] = useState(true);
    const navigate = useNavigate()

    useEffect(() => {
        const getClientSecret = async () => {
            const response = await axios({
                method: 'post',
                url: `/payments/create?total=${basket?.reduce((amount, item) => item.price + amount, 0) * 100}`
            })
            setClientSecret(response.data.clientSecret)
        }
    }, basket)
    console.log("Cliet SECRET -->",clientSecret)
    const handleSubmit = async (e) => {
        e.preventDefault();
        setProcessing(true);

        const payload = await stripe.confirmCardPayment(clientSecret, {payment_method:{
            card: elements.getElement(CardElement)
        }
    }).then(({paymentIntent}) => {
        setSucceeded(true);
        setError(null);
        setProcessing(false)

        navigate('/orders')
    })

    }

    const handleChange = e => {

    }
  return (
    <div className='payment'>


        <div className='payment__container'>
        <h1>
            Checkout (
                <Link to='/checkout'>{basket?.length} items</Link>
            )
        </h1>
            <div className='payment__section'>
                <div className='payment__title'>
                    <h3>Delivery Address</h3>
                </div>
                <div className='payment__address'>
                    <p>{user?.email}</p>
                    <p>Random street</p>
                    <p>Random country, Random</p>
                </div>
            </div>
            <div className='payment__section'>
                <div className='payment__title'>
                    <h3>Review items and delivery</h3>
                </div>
                <div className='payment__items'>
                    {basket.map(item => (
                        <CheckoutProduct
                        id = {item.id}
                        title = {item.title}
                        image = {item.image}
                        price = {item.price}
                        rating = {item.rating}
                        />
                    ))}
                </div>
            </div>
            <div className='payment__section'>
                <div className='payment__title'>
                    <h3>
                        Payment Method
                    </h3>
                </div>
                <div className='payment__details'>
                        <form onSubmit={handleSubmit}>
                            <CardElement onChange={handleChange}></CardElement>
                            <div className='payment__priceContainer'>
                            <CurrencyFormat
                                renderText={(value) => (
                                <>
                                    <p>                        
                                        <strong>Order total: {value}</strong>
                                    </p>

                                </>
                            )}
                            decimalScale={2}
                            value={basket?.reduce((amount, item) => item.price + amount, 0)}
                            displayType={"text"}
                            thousandSeparator={true}
                            prefix={'$'}
                            />
                            <button disabled={processing ||  disabled || succeeded}>
                                <span>{processing ? <p>Processing </p> : 'Buy now'}
                                </span> 

                            </button>

                                {error && <div>{error}</div>}

                            </div>
                        </form>
                </div> 

            </div>
        </div>
    </div>
  )
}

export default Payment
