export const initialState = {
    basket: [],
};
export const getBasketTotal = (basket) => {
  const amount = basket?.reduce((amount, item) => item.price + amount, 0);
  return amount
}

const reducer = (state, action) => {
    console.log(basket)
    switch(action.type){
        case 'ADD_TO_BASKET':
            return {
                ...state,
                basket: [...state.basket, action.item],
            };
        default:
            return state;
    }
};

export default reducer;
